package com.example.demo;import com.example.demo.repository.Dao;import com.example.demo.repository.PostgreSqlDao;import com.example.demo.repository.PostgreSqlDaoTime;import org.springframework.web.bind.annotation.*;import java.util.Optional;@RestController()@RequestMapping("/")public class MetricController {    private static final Dao<Metric, Integer> METRIC_DAO = new PostgreSqlDao();    private static final Dao<MetricTime, Integer> METRIC_DAO_TIME = new PostgreSqlDaoTime();    @PostMapping("/gameVictory")    void testPost(@RequestBody Metric metric) {        Optional<Metric> game = METRIC_DAO.get(metric);        METRIC_DAO.update(metric, Integer.parseInt(game.get().getMetric()));    }    @PostMapping("/time")    void saveTame(@RequestBody MetricTime metric) {        long timeInGame = (metric.getTimeFinish().getTime().getTime()- metric.getTimeStart().getTime().getTime()) / (60 * 1000);        metric.setTimeInGame(Long.toString(timeInGame));        METRIC_DAO_TIME.save(metric);    }}